import React from "react";
import { useState } from "react";
import { useDispatch } from "react-redux";
import { getCountryByName } from "../actions";

export default function SearchBar (){
    const dispatch = useDispatch()
    const [country, setCountry] = useState('')
    const handleOnChange = (e)=>{
        e.preventDefault()
        if(country !== e.target.value) setCountry(e.target.value.toLowerCase())
        }
    const handleSubmit =(e) =>{
        e.preventDefault()
        dispatch(getCountryByName(country))
    }
    const handleKeyPress = (e) =>{
        if(e.key === 'Enter') {
            dispatch(getCountryByName(country))
            setCountry('')
        
        }
    }

    return (
        <div className="wrapper">
            <input id="input" type='text' placeholder="Search..." 
            onChange={(e)=>{handleOnChange(e)}} 
            onKeyPress={e=>{handleKeyPress(e)}} />
            <button type='submit' onClick={(e)=>{handleSubmit(e)}}><img alt="magnifying glass" src="data:image/svg+xml;utf8;base64," /></button>
        </div>
    )
}